package infiniteWhile;

public class InfiniteWhileLoop1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		while(true) {
			//while loop is executed if the condition is true else the while loop is terminated.
			//in Over case we have entered condition as true, so condition will be never false 
			// and loop will run Infinite times
			
			System.out.println("Infinite While 1");
		}

	}

}
